import React from 'react';
import './App.css';
import Header from "./components/Header"
import Navigation from "./components/Navigation"
import Main from "./components/Main"
import Contact from "./components/Contact"

function App() {
    const [language, setLanguage] = React.useState(false)
    const [enabled, setEnabled] = React.useState({
        start: true,
        aboutMe: false,
        cv: false,
        skills: false
    })

    function toggleLanguage() {
        setLanguage(!language)
    }

    function toggleEnabled(id) {
        setEnabled(prev => {
            if (prev.start){
                return {...prev, start: !prev.start}
            } else if (prev.aboutMe){
                return {...prev, aboutMe: !prev.aboutMe}
            } else if (prev.cv){
                return {...prev, cv: !prev.cv}
            } else if (prev.skills){
                return {...prev, skills: !prev.skills}
            } else {return prev}
        })

        setEnabled(prev => {
            return {...prev, [id]: !prev[id]}
        })
    }
    console.log(enabled)

    return (
        <div>
            <Header />
            <Main id={enabled} />
            <div className="lower-part">
                <Contact />
                <Navigation id={enabled} toggleEnabled={toggleEnabled}/>
            </div>
            <div className="language-div">
                <button className="language-button" onClick={toggleLanguage}><span class="fi fi-us"></span></button>
            </div>
        </div>
    );
}

export default App;
